import React, { useState } from 'react';
import { ArrowLeft, Plus, Copy, Trash2, Clock, AlertTriangle } from 'lucide-react';
import { useData } from '../context/DataContext';
import SharedFilters from './SharedFilters';
import { generateDailyReportWhatsApp } from '../utils/whatsappGenerator';

const DailyReportScreen = ({ onBack, quickMode = false }) => {
  const { data, addAtividade } = useData();

  const [filters, setFilters] = useState({
    data: new Date().toISOString().split('T')[0],
    clusterId: '',
    usinaId: '',
    tecnicoIds: [], // Múltiplos técnicos
    funcaoId: ''
  });

  const [atividadesPorUsina, setAtividadesPorUsina] = useState({}); // Objeto: { usinaId: [atividades] }
  const [tcuComFalha, setTcuComFalha] = useState({ 
    tem: false, 
    falhas: [] // Array de objetos { skid: '', tracker: '' }
  });
  const [observacoesGerais, setObservacoesGerais] = useState('');
  const [usinaSelecionada, setUsinaSelecionada] = useState('');

  // Validações
  const [errors, setErrors] = useState({});
  const [overlaps, setOverlaps] = useState([]);

  // Verificar se os filtros básicos estão completos (não precisa mais de usina específica)
  const filtersComplete = filters.data && filters.clusterId && filters.tecnicoIds && filters.tecnicoIds.length > 0;

  // Funções para visão geral por cluster
  const getClustersComRelatorios = () => {
    if (!filters.data) return [];

    const relatoriosPorCluster = new Map();

    data.atividades.forEach(atividade => {
      if (atividade.data === filters.data) {
        const cluster = data.clusters.find(c => c.id === atividade.clusterId);
        if (cluster) {
          if (!relatoriosPorCluster.has(cluster.id)) {
            relatoriosPorCluster.set(cluster.id, {
              cluster: cluster,
              atividades: [],
              tecnicos: new Set()
            });
          }

          relatoriosPorCluster.get(cluster.id).atividades.push(atividade);
          relatoriosPorCluster.get(cluster.id).tecnicos.add(atividade.tecnicoId);
        }
      }
    });

    return Array.from(relatoriosPorCluster.values());
  };

  const getAtividadesDoCluster = (clusterId) => {
    return data.atividades.filter(atividade => 
      atividade.data === filters.data && atividade.clusterId === clusterId
    );
  };

  // Adicionar nova usina para atividades
  const addNovaUsina = () => {
    if (!usinaSelecionada) {
      alert('Selecione uma usina primeiro');
      return;
    }

    if (atividadesPorUsina[usinaSelecionada]) {
      alert('Esta usina já foi adicionada. Use a seção existente.');
      return;
    }

    setAtividadesPorUsina(prev => ({
      ...prev,
      [usinaSelecionada]: [{
        tarefa: '',
        inicio: '',
        fim: '',
        observacoes: ''
      }]
    }));

    setUsinaSelecionada('');
  };

  // Adicionar nova atividade em uma usina específica
  const addNewAtividadeNaUsina = (usinaId) => {
    const newAtividade = {
      tarefa: '',
      inicio: '',
      fim: '',
      observacoes: ''
    };

    setAtividadesPorUsina(prev => ({
      ...prev,
      [usinaId]: [...(prev[usinaId] || []), newAtividade]
    }));
  };

  // Atualizar atividade
  const updateAtividadeLocal = (index, field, value) => {
    const updated = [...atividades];
    updated[index] = { ...updated[index], [field]: value };
    setAtividades(updated);

    // Validar sobreposições
    validateOverlaps(updated);
  };

  // Remover atividade
  const removeAtividade = (index) => {
    const updated = atividades.filter((_, i) => i !== index);
    setAtividades(updated);
    validateOverlaps(updated);
  };

  // Duplicar atividade
  const duplicateAtividade = (index) => {
    const atividade = atividades[index];
    const duplicated = {
      ...atividade,
      tarefa: `${atividade.tarefa} (cópia)`
    };
    const updated = [...atividades];
    updated.splice(index + 1, 0, duplicated);
    setAtividades(updated);
    validateOverlaps(updated);
  };

  // Validar sobreposições de horários
  const validateOverlaps = (atividadesList) => {
    const overlaps = [];
    const sortedActivities = atividadesList
      .filter(a => a.inicio && a.fim)
      .sort((a, b) => a.inicio.localeCompare(b.inicio));

    for (let i = 0; i < sortedActivities.length - 1; i++) {
      const current = sortedActivities[i];
      const next = sortedActivities[i + 1];

      if (current.fim > next.inicio) {
        overlaps.push({ index1: i, index2: i + 1 });
      }
    }

    setOverlaps(overlaps);
  };

  // Validar campos obrigatórios
  const validateForm = () => {
    const newErrors = {};

    if (!filtersComplete) {
      newErrors.filters = 'Preencha todos os filtros obrigatórios';
    }

    atividades.forEach((atividade, index) => {
      if (!atividade.tarefa.trim()) {
        newErrors[`tarefa_${index}`] = 'Tarefa é obrigatória';
      }
      if (!atividade.inicio) {
        newErrors[`inicio_${index}`] = 'Horário de início é obrigatório';
      }
      if (!atividade.fim) {
        newErrors[`fim_${index}`] = 'Horário de fim é obrigatório';
      }
      if (atividade.inicio && atividade.fim && atividade.inicio >= atividade.fim) {
        newErrors[`horario_${index}`] = 'Fim deve ser posterior ao início';
      }
    });

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Salvar relatório
  const saveReport = () => {
    if (!validateForm()) return;

    const reportData = {
      data: filters.data,
      clusterId: filters.clusterId,
      usinaId: filters.usinaId,
      tecnicoIds: filters.tecnicoIds,
      funcaoId: filters.funcaoId,
      atividades: atividades.filter(a => a.tarefa.trim()),
      tcuComFalha,
      observacoesGerais
    };

    // Salvar cada atividade individualmente para cada técnico
    reportData.atividades.forEach(atividade => {
      // Para cada técnico selecionado, criar uma atividade
      filters.tecnicoIds.forEach(tecnicoId => {
        addAtividade({
          ...atividade,
          data: filters.data,
          clusterId: filters.clusterId,
          usinaId: filters.usinaId,
          tecnicoId: tecnicoId, // Salva individualmente para cada técnico
          funcaoId: filters.funcaoId
        });
      });
    });

    alert('Relatório salvo com sucesso!');

    if (!quickMode) {
      onBack();
    }
  };

  // Gerar WhatsApp
  const generateWhatsApp = () => {
    if (!validateForm()) return;

    const cluster = data.clusters.find(c => c.id === filters.clusterId);
    const usina = data.usinas.find(u => u.id === filters.usinaId);

    // Buscar nomes dos técnicos selecionados
    const tecnicosNomes = filters.tecnicoIds
      .map(id => data.tecnicos.find(t => t.id === id)?.nome)
      .filter(nome => nome)
      .join(', ');

    const message = generateDailyReportWhatsApp({
      cluster: cluster?.nome,
      data: filters.data,
      tecnico: tecnicosNomes, // Agora pode ser múltiplos técnicos
      usina: usina?.nome,
      atividades: atividades.filter(a => a.tarefa.trim()),
      tcuComFalha,
      observacoesGerais
    });

    navigator.clipboard.writeText(message).then(() => {
      alert('Mensagem copiada para a área de transferência!');
    }).catch(() => {
      // Fallback para navegadores mais antigos
      const textArea = document.createElement('textarea');
      textArea.value = message;
      document.body.appendChild(textArea);
      textArea.select();
      document.execCommand('copy');
      document.body.removeChild(textArea);
      alert('Mensagem copiada para a área de transferência!');
    });
  };

  return (
    <div className="mobile-container">
      <div className="p-6">
        {/* Header */}
        <div className="flex items-center mb-6">
          <button
            onClick={onBack}
            className="mr-4 p-2 hover:bg-gray-100 rounded-lg transition-colors"
          >
            <ArrowLeft className="w-6 h-6 text-gray-600" />
          </button>
          <div>
            <h1 className="text-xl font-bold text-gray-900">
              Relatório Diário de Atividades
            </h1>
            {quickMode && (
              <p className="text-sm text-blue-600">Modo Rápido Ativado</p>
            )}
          </div>
        </div>

        {/* Filtros */}
        <SharedFilters
          filters={filters}
          onFiltersChange={setFilters}
          clusters={data.clusters}
          usinas={data.usinas}
          tecnicos={data.tecnicos}
          funcoes={data.funcoes}
          showFuncao={true}
          allowMultipleTecnicos={true}
        />

        {/* Erro de filtros */}
        {errors.filters && (
          <div className="bg-red-50 border border-red-200 rounded-lg p-3 mb-4">
            <p className="text-red-700 text-sm">{errors.filters}</p>
          </div>
        )}

        {/* Aviso de sobreposições */}
        {overlaps.length > 0 && (
          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3 mb-4">
            <div className="flex items-start">
              <AlertTriangle className="w-5 h-5 text-yellow-600 mt-0.5 mr-2" />
              <div>
                <p className="text-yellow-800 font-medium text-sm">Atenção: Sobreposição de horários</p>
                <p className="text-yellow-700 text-xs mt-1">
                  Algumas atividades têm horários sobrepostos. Verifique os horários.
                </p>
              </div>
            </div>
          </div>
        )}

        {/* Tabela de Atividades */}
        <div className="card mb-4">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-gray-900 flex items-center">
              <Clock className="w-5 h-5 mr-2 text-primary-600" />
              Atividades Programadas
            </h3>
            <button
              onClick={addNewAtividade}
              className="btn-primary flex items-center text-sm py-2 px-4"
            >
              <Plus className="w-4 h-4 mr-1" />
              Adicionar
            </button>
          </div>

          {atividades.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              <Clock className="w-12 h-12 mx-auto mb-3 opacity-50" />
              <p>Nenhuma atividade adicionada</p>
              <p className="text-sm">Clique em "Adicionar" para começar</p>
            </div>
          ) : (
            <div className="space-y-3">
              {atividades.map((atividade, index) => (
                <div key={index} className="border border-gray-200 rounded-lg p-4">
                  <div className="grid grid-cols-1 gap-3">
                    {/* Tarefa */}
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Tarefa *
                      </label>
                      <input
                        type="text"
                        value={atividade.tarefa}
                        onChange={(e) => updateAtividadeLocal(index, 'tarefa', e.target.value)}
                        placeholder="Ex: Inspeção Inversor 3"
                        className={`input-field ${errors[`tarefa_${index}`] ? 'border-red-500' : ''}`}
                      />
                      {errors[`tarefa_${index}`] && (
                        <p className="text-red-500 text-xs mt-1">{errors[`tarefa_${index}`]}</p>
                      )}
                    </div>

                    {/* Horários */}
                    <div className="grid grid-cols-2 gap-3">
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Início *
                        </label>
                        <input
                          type="time"
                          value={atividade.inicio}
                          onChange={(e) => updateAtividadeLocal(index, 'inicio', e.target.value)}
                          className={`input-field ${errors[`inicio_${index}`] || errors[`horario_${index}`] ? 'border-red-500' : ''}`}
                        />
                      </div>
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Fim *
                        </label>
                        <input
                          type="time"
                          value={atividade.fim}
                          onChange={(e) => updateAtividadeLocal(index, 'fim', e.target.value)}
                          className={`input-field ${errors[`fim_${index}`] || errors[`horario_${index}`] ? 'border-red-500' : ''}`}
                        />
                      </div>
                    </div>

                    {/* Observações */}
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Observações
                      </label>
                      <input
                        type="text"
                        value={atividade.observacoes}
                        onChange={(e) => updateAtividadeLocal(index, 'observacoes', e.target.value)}
                        placeholder="Observações adicionais (opcional)"
                        className="input-field"
                      />
                    </div>

                    {/* Ações */}
                    <div className="flex justify-end space-x-2">
                      <button
                        onClick={() => duplicateAtividade(index)}
                        className="p-2 text-gray-500 hover:text-primary-600 hover:bg-primary-50 rounded-lg transition-colors"
                        title="Duplicar atividade"
                      >
                        <Copy className="w-4 h-4" />
                      </button>
                      <button
                        onClick={() => removeAtividade(index)}
                        className="p-2 text-gray-500 hover:text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                        title="Remover atividade"
                      >
                        <Trash2 className="w-4 h-4" />
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Visão Geral por Cluster */}
        {filters.data && (
          <div className="card mb-4">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Visão Geral por Cluster</h3>
            <p className="text-sm text-gray-600 mb-4">
              Visualize quais clusters já têm relatórios na data selecionada.
            </p>

            <div className="space-y-2">
              {data.clusters.filter(c => c.ativo).map(cluster => {
                const atividadesCluster = getAtividadesDoCluster(cluster.id);
                const hasRelatorios = atividadesCluster.length > 0;
                const tecnicosUnicos = new Set(atividadesCluster.map(a => a.tecnicoId)).size;
                const totalAtividades = atividadesCluster.length;

                return (
                  <div 
                    key={cluster.id} 
                    className={`flex items-center justify-between p-3 rounded-lg cursor-pointer hover:bg-gray-100 transition-colors ${
                      filters.clusterId === cluster.id ? 'bg-primary-50 border-2 border-primary-200' : 'bg-gray-50'
                    }`}
                    onClick={() => {
                      setFilters(prev => ({ ...prev, clusterId: cluster.id, usinaId: '', tecnicoIds: [] }));
                    }}
                  >
                    <div className="flex items-center">
                      <div className={`w-6 h-6 rounded-full flex items-center justify-center mr-3 ${
                        hasRelatorios ? 'bg-green-100' : 'bg-gray-100'
                      }`}>
                        {hasRelatorios ? '✅' : '⭕'}
                      </div>
                      <div>
                        <p className="font-medium text-gray-900">{cluster.nome}</p>
                        {hasRelatorios && (
                          <p className="text-xs text-gray-500">
                            {totalAtividades} atividade(s) • {tecnicosUnicos} técnico(s)
                          </p>
                        )}
                      </div>
                    </div>
                    <div className={`text-sm px-2 py-1 rounded ${
                      hasRelatorios ? 'bg-green-100 text-green-800' : 'bg-orange-100 text-orange-800'
                    }`}>
                      {hasRelatorios ? 'Preenchido' : 'Pendente'}
                    </div>
                  </div>
                );
              })}

              {/* Resumo do progresso */}
              <div className="mt-4 p-3 bg-blue-50 rounded-lg">
                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium text-blue-900">
                    Clusters com relatórios: {getClustersComRelatorios().length} de {data.clusters.filter(c => c.ativo).length}
                  </span>
                  <div className="w-32 bg-blue-200 rounded-full h-2">
                    <div 
                      className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                      style={{ 
                        width: `${data.clusters.filter(c => c.ativo).length > 0 ? (getClustersComRelatorios().length / data.clusters.filter(c => c.ativo).length) * 100 : 0}%` 
                      }}
                    ></div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Atividades Existentes no Cluster */}
        {filters.clusterId && filters.data && (
          <div className="card mb-4">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">
              Atividades Existentes no Cluster
            </h3>

            {(() => {
              const atividadesCluster = getAtividadesDoCluster(filters.clusterId);

              if (atividadesCluster.length === 0) {
                return (
                  <p className="text-sm text-gray-500 text-center py-4">
                    Nenhuma atividade registrada para este cluster na data selecionada
                  </p>
                );
              }

              // Agrupar por usina
              const atividadesPorUsina = {};
              atividadesCluster.forEach(atividade => {
                const usina = data.usinas.find(u => u.id === atividade.usinaId);
                const usinaName = usina?.nome || 'Usina Desconhecida';

                if (!atividadesPorUsina[usinaName]) {
                  atividadesPorUsina[usinaName] = [];
                }
                atividadesPorUsina[usinaName].push(atividade);
              });

              return (
                <div className="space-y-3">
                  {Object.keys(atividadesPorUsina).map(usinaName => (
                    <div key={usinaName} className="border border-gray-200 rounded-lg p-3">
                      <h4 className="font-medium text-gray-800 mb-2">{usinaName}</h4>
                      <div className="space-y-2">
                        {atividadesPorUsina[usinaName].map((atividade, index) => {
                          const tecnico = data.tecnicos.find(t => t.id === atividade.tecnicoId);

                          return (
                            <div key={atividade.id || index} className="bg-gray-50 rounded p-3 text-sm">
                              <div className="flex items-start justify-between">
                                <div className="flex-1">
                                  <div className="flex items-center mb-1">
                                    <span className="font-medium text-gray-900">{atividade.tarefa}</span>
                                    <span className="ml-2 text-xs text-gray-500">
                                      ⏰ {atividade.inicio} - {atividade.fim}
                                    </span>
                                  </div>
                                  <div className="flex items-center text-xs text-gray-600">
                                    <span>👷 {tecnico?.nome || 'Técnico não encontrado'}</span>
                                    {atividade.observacoes && (
                                      <span className="ml-2">• {atividade.observacoes}</span>
                                    )}
                                  </div>
                                </div>
                                <button
                                  onClick={() => {
                                    // Carregar atividade para edição
                                    setFilters(prev => ({ 
                                      ...prev, 
                                      clusterId: atividade.clusterId,
                                      usinaId: atividade.usinaId,
                                      tecnicoIds: [atividade.tecnicoId]
                                    }));

                                    // Carregar atividade específica no formulário
                                    setAtividades([{
                                      id: atividade.id,
                                      tarefa: atividade.tarefa,
                                      inicio: atividade.inicio,
                                      fim: atividade.fim,
                                      observacoes: atividade.observacoes || ''
                                    }]);
                                  }}
                                  className="text-primary-600 hover:text-primary-800 text-xs font-medium"
                                >
                                  Editar
                                </button>
                              </div>
                            </div>
                          );
                        })}
                      </div>
                    </div>
                  ))}
                </div>
              );
            })()}
          </div>
        )}

        {/* Observações Gerais */}
        <div className="card mb-4">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Observações Gerais</h3>
          <textarea
            value={observacoesGerais}
            onChange={(e) => setObservacoesGerais(e.target.value)}
            placeholder="Observações gerais do dia..."
            className="input-field h-20 resize-none"
          />
        </div>

        {/* TCU com falha */}
        <div className="card mb-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">TCU com falha</h3>

          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Há TCU com falha?
              </label>
              <div className="flex items-center space-x-4">
                <label className="flex items-center">
                  <input
                    type="radio"
                    name="tcuFalha"
                    checked={!tcuComFalha.tem}
                    onChange={() => setTcuComFalha({ tem: false, falhas: [] })}
                    className="mr-2"
                  />
                  <span className="text-sm">Não</span>
                </label>
                <label className="flex items-center">
                  <input
                    type="radio"
                    name="tcuFalha"
                    checked={tcuComFalha.tem}
                    onChange={() => setTcuComFalha({ tem: true, falhas: tcuComFalha.falhas.length > 0 ? tcuComFalha.falhas : [{ skid: '', tracker: '' }] })}
                    className="mr-2"
                  />
                  <span className="text-sm">Sim</span>
                </label>
              </div>
            </div>

            {tcuComFalha.tem && (
              <div className="space-y-3">
                <div className="flex items-center justify-between">
                  <label className="block text-sm font-medium text-gray-700">
                    Falhas identificadas
                  </label>
                  <button
                    type="button"
                    onClick={() => {
                      setTcuComFalha({
                        ...tcuComFalha,
                        falhas: [...tcuComFalha.falhas, { skid: '', tracker: '' }]
                      });
                    }}
                    className="btn-primary text-xs py-1 px-3"
                  >
                    <Plus className="w-3 h-3 mr-1" />
                    Adicionar
                  </button>
                </div>

                {tcuComFalha.falhas.map((falha, index) => (
                  <div key={index} className="border border-gray-200 rounded-lg p-3">
                    <div className="grid grid-cols-2 gap-3">
                      <div>
                        <label className="block text-xs font-medium text-gray-700 mb-1">
                          Skid
                        </label>
                        <input
                          type="text"
                          value={falha.skid}
                          onChange={(e) => {
                            const newFalhas = [...tcuComFalha.falhas];
                            newFalhas[index].skid = e.target.value;
                            setTcuComFalha({ ...tcuComFalha, falhas: newFalhas });
                          }}
                          placeholder="Ex: Skid 01"
                          className="input-field text-sm"
                        />
                      </div>
                      <div>
                        <label className="block text-xs font-medium text-gray-700 mb-1">
                          Tracker
                        </label>
                        <input
                          type="text"
                          value={falha.tracker}
                          onChange={(e) => {
                            const newFalhas = [...tcuComFalha.falhas];
                            newFalhas[index].tracker = e.target.value;
                            setTcuComFalha({ ...tcuComFalha, falhas: newFalhas });
                          }}
                          placeholder="Ex: Tracker 15"
                          className="input-field text-sm"
                        />
                      </div>
                    </div>
                    {tcuComFalha.falhas.length > 1 && (
                      <div className="flex justify-end mt-2">
                        <button
                          type="button"
                          onClick={() => {
                            const newFalhas = tcuComFalha.falhas.filter((_, i) => i !== index);
                            setTcuComFalha({ ...tcuComFalha, falhas: newFalhas });
                          }}
                          className="text-red-600 hover:text-red-800 text-xs"
                        >
                          Remover
                        </button>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Ações */}
        <div className="space-y-3">
          <button
            onClick={generateWhatsApp}
            disabled={!filtersComplete || atividades.length === 0}
            className="btn-primary w-full flex items-center justify-center"
          >
            <Copy className="w-5 h-5 mr-2" />
            Gerar WhatsApp
          </button>

          <button
            onClick={saveReport}
            disabled={!filtersComplete || atividades.length === 0}
            className="btn-secondary w-full"
          >
            Salvar Relatório
          </button>
        </div>
      </div>
    </div>
  );
};

export default DailyReportScreen;
