import React, { useState } from 'react';
import { ArrowLeft, Clock, Zap, Database, Settings, Plus, Copy, Trash2, AlertTriangle } from 'lucide-react';
import { useData } from '../context/DataContext';
import SharedFilters from './SharedFilters';
import { 
  generateDailyReportWhatsApp,
  generateTechnicalStatusWhatsApp 
} from '../utils/whatsappGenerator';

const QuickModeScreen = ({ onBack }) => {
  const { data, addAtividade, addStatusTecnico } = useData();

  const [filters, setFilters] = useState({
    data: new Date().toISOString().split('T')[0],
    clusterId: '',
    tecnicoIds: []
  });

  // Estado do relatório diário
  const [atividadesPorUsina, setAtividadesPorUsina] = useState({});
  const [tcuComFalha, setTcuComFalha] = useState({ 
    tem: false, 
    falhas: []
  });
  const [observacoesGerais, setObservacoesGerais] = useState('');
  const [usinaSelecionada, setUsinaSelecionada] = useState('');

  // Estado do status técnico
  const [statusData, setStatusData] = useState({
    inversores: { ok100: true, motivo: '', acaoPrevista: '' },
    strings: { ok100: true, motivo: '', acaoPrevista: '' },
    trackers: { ok100: true, motivo: '', acaoPrevista: '' },
    observacoesGeraisTecnico: ''
  });

  const [activeTab, setActiveTab] = useState('atividades');
  const [errors, setErrors] = useState({});
  const [overlaps, setOverlaps] = useState([]);

  // Verificar se os filtros estão completos
  const filtersComplete = filters.data && filters.clusterId && filters.usinaId && filters.tecnicoId;

  // Funções para atividades (mesmo código do DailyReportScreen)
  const addNewAtividade = () => {
    const newAtividade = {
      tarefa: '',
      inicio: '',
      fim: '',
      observacoes: ''
    };
    setAtividades([...atividades, newAtividade]);
  };

  const updateAtividadeLocal = (index, field, value) => {
    const updated = [...atividades];
    updated[index] = { ...updated[index], [field]: value };
    setAtividades(updated);
    validateOverlaps(updated);
  };

  const removeAtividade = (index) => {
    const updated = atividades.filter((_, i) => i !== index);
    setAtividades(updated);
    validateOverlaps(updated);
  };

  const duplicateAtividade = (index) => {
    const atividade = atividades[index];
    const duplicated = {
      ...atividade,
      tarefa: `${atividade.tarefa} (cópia)`
    };
    const updated = [...atividades];
    updated.splice(index + 1, 0, duplicated);
    setAtividades(updated);
    validateOverlaps(updated);
  };

  const validateOverlaps = (atividadesList) => {
    const overlaps = [];
    const sortedActivities = atividadesList
      .filter(a => a.inicio && a.fim)
      .sort((a, b) => a.inicio.localeCompare(b.inicio));

    for (let i = 0; i < sortedActivities.length - 1; i++) {
      const current = sortedActivities[i];
      const next = sortedActivities[i + 1];

      if (current.fim > next.inicio) {
        overlaps.push({ index1: i, index2: i + 1 });
      }
    }

    setOverlaps(overlaps);
  };

  // Funções para status técnico
  const updateSubsystemStatus = (subsystem, field, value) => {
    setStatusData(prev => ({
      ...prev,
      [subsystem]: {
        ...prev[subsystem],
        [field]: value
      }
    }));
  };

  // Validar formulário
  const validateForm = () => {
    const newErrors = {};

    if (!filtersComplete) {
      newErrors.filters = 'Preencha todos os filtros obrigatórios';
    }

    // Validar atividades se há alguma preenchida
    if (atividades.length > 0) {
      atividades.forEach((atividade, index) => {
        if (!atividade.tarefa.trim()) {
          newErrors[`tarefa_${index}`] = 'Tarefa é obrigatória';
        }
        if (!atividade.inicio) {
          newErrors[`inicio_${index}`] = 'Horário de início é obrigatório';
        }
        if (!atividade.fim) {
          newErrors[`fim_${index}`] = 'Horário de fim é obrigatório';
        }
        if (atividade.inicio && atividade.fim && atividade.inicio >= atividade.fim) {
          newErrors[`horario_${index}`] = 'Fim deve ser posterior ao início';
        }
      });
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Salvar ambos os relatórios
  const saveBothReports = () => {
    if (!validateForm()) return;

    // Salvar atividades se houver
    if (atividades.length > 0) {
      atividades.filter(a => a.tarefa.trim()).forEach(atividade => {
        addAtividade({
          ...atividade,
          ...filters
        });
      });
    }

    // Salvar status técnico
    const statusToSave = {
      ...filters,
      ...statusData
    };

    addStatusTecnico(statusToSave);

    alert('Ambos os relatórios foram salvos com sucesso!');
    onBack();
  };

  // Gerar WhatsApp do relatório diário
  const generateDailyWhatsApp = () => {
    if (!filtersComplete || atividades.length === 0) {
      alert('Preencha os filtros e adicione pelo menos uma atividade');
      return;
    }

    const cluster = data.clusters.find(c => c.id === filters.clusterId);
    const usina = data.usinas.find(u => u.id === filters.usinaId);
    const tecnico = data.tecnicos.find(t => t.id === filters.tecnicoId);

    const message = generateDailyReportWhatsApp({
      cluster: cluster?.nome,
      data: filters.data,
      tecnico: tecnico?.nome,
      usina: usina?.nome,
      atividades: atividades.filter(a => a.tarefa.trim()),
      alarmesCriticos,
      observacoesGerais
    });

    copyToClipboard(message);
  };

  // Gerar WhatsApp do status técnico
  const generateTechnicalWhatsApp = () => {
    if (!filtersComplete) {
      alert('Preencha todos os filtros obrigatórios');
      return;
    }

    const cluster = data.clusters.find(c => c.id === filters.clusterId);
    const usina = data.usinas.find(u => u.id === filters.usinaId);
    const tecnico = data.tecnicos.find(t => t.id === filters.tecnicoId);

    const message = generateTechnicalStatusWhatsApp({
      usina: usina?.nome,
      data: filters.data,
      cluster: cluster?.nome,
      tecnico: tecnico?.nome,
      inversores: statusData.inversores,
      strings: statusData.strings,
      trackers: statusData.trackers,
      observacoesGerais: statusData.observacoesGeraisTecnico
    });

    copyToClipboard(message);
  };

  // Copiar para área de transferência
  const copyToClipboard = (message) => {
    navigator.clipboard.writeText(message).then(() => {
      alert('Mensagem copiada para a área de transferência!');
    }).catch(() => {
      const textArea = document.createElement('textarea');
      textArea.value = message;
      document.body.appendChild(textArea);
      textArea.select();
      document.execCommand('copy');
      document.body.removeChild(textArea);
      alert('Mensagem copiada para a área de transferência!');
    });
  };

  // Componente de card de subsistema
  const SubsystemCard = ({ title, icon, subsystem, data }) => {
    const isOk = data.ok100;

    return (
      <div className="border border-gray-200 rounded-lg p-4">
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center">
            <div className={`w-8 h-8 rounded-full flex items-center justify-center mr-3 ${
              isOk ? 'bg-green-100 text-green-600' : 'bg-red-100 text-red-600'
            }`}>
              {icon}
            </div>
            <h3 className="font-semibold text-gray-900">{title}</h3>
          </div>
          <div className={`px-3 py-1 rounded-full text-sm font-medium ${
            isOk ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
          }`}>
            {isOk ? '100%' : '< 100%'}
          </div>
        </div>

        <div className="space-y-3">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Disponibilidade 100%?
            </label>
            <div className="flex items-center space-x-4">
              <label className="flex items-center">
                <input
                  type="radio"
                  checked={isOk}
                  onChange={() => updateSubsystemStatus(subsystem, 'ok100', true)}
                  className="mr-2"
                />
                <span className="text-sm">Sim</span>
              </label>
              <label className="flex items-center">
                <input
                  type="radio"
                  checked={!isOk}
                  onChange={() => updateSubsystemStatus(subsystem, 'ok100', false)}
                  className="mr-2"
                />
                <span className="text-sm">Não</span>
              </label>
            </div>
          </div>

          {!isOk && (
            <>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Motivo
                </label>
                <input
                  type="text"
                  value={data.motivo}
                  onChange={(e) => updateSubsystemStatus(subsystem, 'motivo', e.target.value)}
                  placeholder="Descreva o motivo..."
                  className="input-field"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Ação prevista
                </label>
                <input
                  type="text"
                  value={data.acaoPrevista}
                  onChange={(e) => updateSubsystemStatus(subsystem, 'acaoPrevista', e.target.value)}
                  placeholder="Descreva a ação prevista..."
                  className="input-field"
                />
              </div>
            </>
          )}
        </div>
      </div>
    );
  };

  return (
    <div className="mobile-container">
      <div className="p-6">
        {/* Header */}
        <div className="flex items-center mb-6">
          <button
            onClick={onBack}
            className="mr-4 p-2 hover:bg-gray-100 rounded-lg transition-colors"
          >
            <ArrowLeft className="w-6 h-6 text-gray-600" />
          </button>
          <div>
            <h1 className="text-xl font-bold text-gray-900">
              Modo Rápido - Relatórios Combinados
            </h1>
            <p className="text-sm text-blue-600">Preencha ambos os relatórios com filtros compartilhados</p>
          </div>
        </div>

        {/* Filtros Compartilhados */}
        <SharedFilters
          filters={filters}
          onFiltersChange={setFilters}
          clusters={data.clusters}
          usinas={data.usinas}
          tecnicos={data.tecnicos}
          funcoes={data.funcoes}
          showFuncao={false}
          showUsina={false}
          allowMultipleTecnicos={true}
        />

        {/* Erro de filtros */}
        {errors.filters && (
          <div className="bg-red-50 border border-red-200 rounded-lg p-3 mb-4">
            <p className="text-red-700 text-sm">{errors.filters}</p>
          </div>
        )}

        {/* Aviso de sobreposições */}
        {overlaps.length > 0 && (
          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3 mb-4">
            <div className="flex items-start">
              <AlertTriangle className="w-5 h-5 text-yellow-600 mt-0.5 mr-2" />
              <div>
                <p className="text-yellow-800 font-medium text-sm">Atenção: Sobreposição de horários</p>
                <p className="text-yellow-700 text-xs mt-1">
                  Algumas atividades têm horários sobrepostos. Verifique os horários.
                </p>
              </div>
            </div>
          </div>
        )}

        {/* Tabs */}
        <div className="flex bg-gray-100 rounded-lg p-1 mb-6">
          <button
            onClick={() => setActiveTab('atividades')}
            className={`flex-1 py-3 px-4 rounded-md text-sm font-medium transition-colors ${
              activeTab === 'atividades'
                ? 'bg-white text-primary-600 shadow-sm'
                : 'text-gray-500 hover:text-gray-700'
            }`}
          >
            <Clock className="w-4 h-4 inline mr-2" />
            Atividades
          </button>
          <button
            onClick={() => setActiveTab('status')}
            className={`flex-1 py-3 px-4 rounded-md text-sm font-medium transition-colors ${
              activeTab === 'status'
                ? 'bg-white text-primary-600 shadow-sm'
                : 'text-gray-500 hover:text-gray-700'
            }`}
          >
            <Zap className="w-4 h-4 inline mr-2" />
            Status de Equipamentos
          </button>
        </div>

        {/* Conteúdo das Abas */}
        {activeTab === 'atividades' && (
          <>
            {/* Tabela de Atividades */}
            <div className="card mb-4">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold text-gray-900 flex items-center">
                  <Clock className="w-5 h-5 mr-2 text-primary-600" />
                  Atividades Programadas
                </h3>
                <button
                  onClick={addNewAtividade}
                  className="btn-primary flex items-center text-sm py-2 px-4"
                >
                  <Plus className="w-4 h-4 mr-1" />
                  Adicionar
                </button>
              </div>

              {atividades.length === 0 ? (
                <div className="text-center py-8 text-gray-500">
                  <Clock className="w-12 h-12 mx-auto mb-3 opacity-50" />
                  <p>Nenhuma atividade adicionada</p>
                  <p className="text-sm">Clique em "Adicionar" para começar</p>
                </div>
              ) : (
                <div className="space-y-3">
                  {atividades.map((atividade, index) => (
                    <div key={index} className="border border-gray-200 rounded-lg p-4">
                      <div className="grid grid-cols-1 gap-3">
                        <div>
                          <label className="block text-sm font-medium text-gray-700 mb-1">
                            Tarefa *
                          </label>
                          <input
                            type="text"
                            value={atividade.tarefa}
                            onChange={(e) => updateAtividadeLocal(index, 'tarefa', e.target.value)}
                            placeholder="Ex: Inspeção Inversor 3"
                            className={`input-field ${errors[`tarefa_${index}`] ? 'border-red-500' : ''}`}
                          />
                        </div>

                        <div className="grid grid-cols-2 gap-3">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                              Início *
                            </label>
                            <input
                              type="time"
                              value={atividade.inicio}
                              onChange={(e) => updateAtividadeLocal(index, 'inicio', e.target.value)}
                              className="input-field"
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                              Fim *
                            </label>
                            <input
                              type="time"
                              value={atividade.fim}
                              onChange={(e) => updateAtividadeLocal(index, 'fim', e.target.value)}
                              className="input-field"
                            />
                          </div>
                        </div>

                        <div>
                          <label className="block text-sm font-medium text-gray-700 mb-1">
                            Observações
                          </label>
                          <input
                            type="text"
                            value={atividade.observacoes}
                            onChange={(e) => updateAtividadeLocal(index, 'observacoes', e.target.value)}
                            placeholder="Observações adicionais (opcional)"
                            className="input-field"
                          />
                        </div>

                        <div className="flex justify-end space-x-2">
                          <button
                            onClick={() => duplicateAtividade(index)}
                            className="p-2 text-gray-500 hover:text-primary-600 hover:bg-primary-50 rounded-lg transition-colors"
                            title="Duplicar atividade"
                          >
                            <Copy className="w-4 h-4" />
                          </button>
                          <button
                            onClick={() => removeAtividade(index)}
                            className="p-2 text-gray-500 hover:text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                            title="Remover atividade"
                          >
                            <Trash2 className="w-4 h-4" />
                          </button>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* Observações e Alarmes */}
            <div className="space-y-4 mb-6">
              <div className="card">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Observações Gerais</h3>
                <textarea
                  value={observacoesGerais}
                  onChange={(e) => setObservacoesGerais(e.target.value)}
                  placeholder="Observações gerais do dia..."
                  className="input-field h-20 resize-none"
                />
              </div>

              <div className="card">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Status Sistemas</h3>
                <div className="space-y-3">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Alarmes críticos
                    </label>
                    <div className="flex items-center space-x-4">
                      <label className="flex items-center">
                        <input
                          type="radio"
                          name="alarmes"
                          checked={!alarmesCriticos.tem}
                          onChange={() => setAlarmesCriticos({ tem: false, descricao: '' })}
                          className="mr-2"
                        />
                        <span className="text-sm">Não</span>
                      </label>
                      <label className="flex items-center">
                        <input
                          type="radio"
                          name="alarmes"
                          checked={alarmesCriticos.tem}
                          onChange={() => setAlarmesCriticos({ tem: true, descricao: '' })}
                          className="mr-2"
                        />
                        <span className="text-sm">Sim</span>
                      </label>
                    </div>
                  </div>

                  {alarmesCriticos.tem && (
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Descrição dos alarmes
                      </label>
                      <input
                        type="text"
                        value={alarmesCriticos.descricao}
                        onChange={(e) => setAlarmesCriticos({ ...alarmesCriticos, descricao: e.target.value })}
                        placeholder="Ex: Alarme no inversor 3, verificação às 14h"
                        className="input-field"
                      />
                    </div>
                  )}
                </div>
              </div>
            </div>

            {/* Botão para gerar WhatsApp das atividades */}
            <button
              onClick={generateDailyWhatsApp}
              disabled={!filtersComplete || atividades.length === 0}
              className="btn-primary w-full flex items-center justify-center mb-4"
            >
              <Copy className="w-5 h-5 mr-2" />
              Gerar WhatsApp - Relatório Diário
            </button>
          </>
        )}

        {activeTab === 'status' && (
          <>
            {/* Cards de Subsistemas */}
            <div className="space-y-4 mb-6">
              <SubsystemCard
                title="Inversores"
                icon={<Zap className="w-4 h-4" />}
                subsystem="inversores"
                data={statusData.inversores}
              />

              <SubsystemCard
                title="Strings"
                icon={<Database className="w-4 h-4" />}
                subsystem="strings"
                data={statusData.strings}
              />

              <SubsystemCard
                title="Trackers"
                icon={<Settings className="w-4 h-4" />}
                subsystem="trackers"
                data={statusData.trackers}
              />
            </div>

            {/* Observações Gerais do Status de Equipamentos */}
            <div className="card mb-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Observações de Equipamentos</h3>
              <textarea
                value={statusData.observacoesGeraisTecnico}
                onChange={(e) => setStatusData(prev => ({ ...prev, observacoesGeraisTecnico: e.target.value }))}
                placeholder="Observações gerais do status técnico..."
                className="input-field h-20 resize-none"
              />
            </div>

            {/* Botão para gerar WhatsApp do status técnico */}
            <button
              onClick={generateTechnicalWhatsApp}
              disabled={!filtersComplete}
              className="btn-primary w-full flex items-center justify-center mb-4"
            >
              <Copy className="w-5 h-5 mr-2" />
              Gerar WhatsApp - Status de Equipamentos
            </button>
          </>
        )}

        {/* Ações Finais */}
        <div className="space-y-3 pt-4 border-t border-gray-200">
          <button
            onClick={saveBothReports}
            disabled={!filtersComplete}
            className="btn-primary w-full text-lg py-4"
          >
            💾 Salvar Ambos os Relatórios
          </button>

          <p className="text-xs text-center text-gray-500">
            O modo rápido permite preencher e salvar ambos os relatórios de uma só vez
          </p>
        </div>
      </div>
    </div>
  );
};

export default QuickModeScreen;
