import React, { useState } from 'react';
import { ArrowLeft, Zap, Database, Settings, Copy, Printer } from 'lucide-react';
import { useData } from '../context/DataContext';
import SharedFilters from './SharedFilters';
import { 
  generateTechnicalStatusWhatsApp, 
  generateConsolidatedTechnicalStatusWhatsApp 
} from '../utils/whatsappGenerator';

const TechnicalStatusScreen = ({ onBack, quickMode = false }) => {
  const { data, addStatusTecnico } = useData();

  const [filters, setFilters] = useState({
    data: new Date().toISOString().split('T')[0],
    clusterId: '',
    usinaId: '',
    tecnicoId: ''
  });

  const [statusData, setStatusData] = useState({
    inversores: { ok100: true, motivo: '', acaoPrevista: '' },
    strings: { ok100: true, motivo: '', acaoPrevista: '' },
    trackers: { ok100: true, motivo: '', acaoPrevista: '' },
    observacoesGerais: ''
  });

  const [errors, setErrors] = useState({});

  // Verificar se os filtros estão completos
  const filtersComplete = filters.data && filters.clusterId && filters.usinaId && filters.tecnicoId;

  // Atualizar status de um subsistema (usando useCallback para evitar re-renderização)
  const updateSubsystemStatus = React.useCallback((subsystem, field, value) => {
    setStatusData(prev => ({
      ...prev,
      [subsystem]: {
        ...prev[subsystem],
        [field]: value
      }
    }));
  }, []);

  // Validar formulário
  const validateForm = () => {
    const newErrors = {};

    if (!filtersComplete) {
      newErrors.filters = 'Preencha todos os filtros obrigatórios';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Salvar status técnico
  const saveStatus = () => {
    if (!validateForm()) return;

    const statusToSave = {
      ...filters,
      ...statusData
    };

    addStatusTecnico(statusToSave);
    alert('Status técnico salvo com sucesso!');

    if (!quickMode) {
      onBack();
    }
  };

  // Gerar WhatsApp individual
  const generateIndividualWhatsApp = () => {
    if (!validateForm()) return;

    const cluster = data.clusters.find(c => c.id === filters.clusterId);
    const usina = data.usinas.find(u => u.id === filters.usinaId);
    const tecnico = data.tecnicos.find(t => t.id === filters.tecnicoId);

    const message = generateTechnicalStatusWhatsApp({
      usina: usina?.nome,
      data: filters.data,
      cluster: cluster?.nome,
      tecnico: tecnico?.nome,
      ...statusData
    });

    copyToClipboard(message);
  };

  // Gerar WhatsApp consolidado por cluster
  const generateConsolidatedWhatsApp = () => {
    if (!filters.clusterId || !filters.data) {
      alert('Selecione um cluster e uma data para gerar o relatório consolidado');
      return;
    }

    const cluster = data.clusters.find(c => c.id === filters.clusterId);
    const usinasDoCluster = data.usinas.filter(u => u.clusterId === filters.clusterId);

    // Buscar status de todas as usinas do cluster na data selecionada
    const statusDoCluster = data.statusTecnico.filter(status => 
      status.data === filters.data && 
      usinasDoCluster.some(u => u.id === status.usinaId)
    );

    if (statusDoCluster.length === 0) {
      alert('Nenhum status encontrado para este cluster na data selecionada');
      return;
    }

    // Enriquecer dados com nomes
    const statusEnriquecido = statusDoCluster.map(status => {
      const usina = data.usinas.find(u => u.id === status.usinaId);
      const tecnico = data.tecnicos.find(t => t.id === status.tecnicoId);

      return {
        ...status,
        usina: usina?.nome,
        tecnico: tecnico?.nome,
        cluster: cluster?.nome
      };
    });

    const message = generateConsolidatedTechnicalStatusWhatsApp(
      statusEnriquecido, 
      cluster?.nome, 
      filters.data
    );

    copyToClipboard(message);
  };

  // Copiar para área de transferência
  const copyToClipboard = (message) => {
    navigator.clipboard.writeText(message).then(() => {
      alert('Mensagem copiada para a área de transferência!');
    }).catch(() => {
      // Fallback para navegadores mais antigos
      const textArea = document.createElement('textarea');
      textArea.value = message;
      document.body.appendChild(textArea);
      textArea.select();
      document.execCommand('copy');
      document.body.removeChild(textArea);
      alert('Mensagem copiada para a área de transferência!');
    });
  };

  // Componente de card de subsistema (movido para fora para evitar re-renderização)
  const SubsystemCard = React.memo(({ title, icon, subsystem, data, onUpdate }) => {
    const isOk = data.ok100;

    return (
      <div className="border border-gray-200 rounded-lg p-4">
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center">
            <div className={`w-8 h-8 rounded-full flex items-center justify-center mr-3 ${
              isOk ? 'bg-green-100 text-green-600' : 'bg-red-100 text-red-600'
            }`}>
              {icon}
            </div>
            <h3 className="font-semibold text-gray-900">{title}</h3>
          </div>
          <div className={`px-3 py-1 rounded-full text-sm font-medium ${
            isOk ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
          }`}>
            {isOk ? '100%' : '< 100%'}
          </div>
        </div>

        <div className="space-y-3">
          {/* Status */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Disponibilidade 100%?
            </label>
            <div className="flex items-center space-x-4">
              <label className="flex items-center">
                <input
                  type="radio"
                  name={`status_${subsystem}`}
                  checked={isOk}
                  onChange={() => onUpdate(subsystem, 'ok100', true)}
                  className="mr-2"
                />
                <span className="text-sm">Sim</span>
              </label>
              <label className="flex items-center">
                <input
                  type="radio"
                  name={`status_${subsystem}`}
                  checked={!isOk}
                  onChange={() => onUpdate(subsystem, 'ok100', false)}
                  className="mr-2"
                />
                <span className="text-sm">Não</span>
              </label>
            </div>
          </div>

          {/* Motivo e Ação (só aparece se não estiver 100%) */}
          {!isOk && (
            <>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Motivo
                </label>
                <input
                  type="text"
                  value={data.motivo || ''}
                  onChange={(e) => onUpdate(subsystem, 'motivo', e.target.value)}
                  placeholder="Descreva o motivo..."
                  className="input-field"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Ação prevista
                </label>
                <input
                  type="text"
                  value={data.acaoPrevista || ''}
                  onChange={(e) => onUpdate(subsystem, 'acaoPrevista', e.target.value)}
                  placeholder="Descreva a ação prevista..."
                  className="input-field"
                />
              </div>
            </>
          )}
        </div>
      </div>
    );
  });

  return (
    <div className="mobile-container">
      <div className="p-6">
        {/* Header */}
        <div className="flex items-center mb-6">
          <button
            onClick={onBack}
            className="mr-4 p-2 hover:bg-gray-100 rounded-lg transition-colors"
          >
            <ArrowLeft className="w-6 h-6 text-gray-600" />
          </button>
          <div>
            <h1 className="text-xl font-bold text-gray-900">
              Relatório de Status Técnico
            </h1>
            {quickMode && (
              <p className="text-sm text-blue-600">Modo Rápido Ativado</p>
            )}
          </div>
        </div>

        {/* Filtros */}
        <SharedFilters
          filters={filters}
          onFiltersChange={setFilters}
          clusters={data.clusters}
          usinas={data.usinas}
          tecnicos={data.tecnicos}
          funcoes={data.funcoes}
          showFuncao={false}
        />

        {/* Erro de filtros */}
        {errors.filters && (
          <div className="bg-red-50 border border-red-200 rounded-lg p-3 mb-4">
            <p className="text-red-700 text-sm">{errors.filters}</p>
          </div>
        )}

        {/* Cards de Subsistemas */}
        <div className="space-y-4 mb-6">
          <SubsystemCard
            title="Inversores"
            icon={<Zap className="w-4 h-4" />}
            subsystem="inversores"
            data={statusData.inversores}
            onUpdate={updateSubsystemStatus}
          />

          <SubsystemCard
            title="Strings"
            icon={<Database className="w-4 h-4" />}
            subsystem="strings"
            data={statusData.strings}
            onUpdate={updateSubsystemStatus}
          />

          <SubsystemCard
            title="Trackers"
            icon={<Settings className="w-4 h-4" />}
            subsystem="trackers"
            data={statusData.trackers}
            onUpdate={updateSubsystemStatus}
          />
        </div>

        {/* Observações Gerais */}
        <div className="card mb-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Observações Gerais</h3>
          <textarea
            value={statusData.observacoesGerais}
            onChange={(e) => setStatusData(prev => ({ ...prev, observacoesGerais: e.target.value }))}
            placeholder="Observações gerais do status técnico..."
            className="input-field h-20 resize-none"
          />
        </div>

        {/* Visão Consolidada */}
        <div className="card mb-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Visão por Cluster</h3>
          <p className="text-sm text-gray-600 mb-4">
            Visualize o status de todas as usinas do cluster selecionado na data escolhida.
          </p>

          {filters.clusterId && filters.data ? (
            <div className="space-y-2">
              {data.usinas
                .filter(u => u.clusterId === filters.clusterId)
                .map(usina => {
                  const statusUsina = data.statusTecnico.find(s => 
                    s.usinaId === usina.id && s.data === filters.data
                  );

                  const hasStatus = !!statusUsina;
                  const allOk = statusUsina ? 
                    statusUsina.inversores.ok100 && 
                    statusUsina.strings.ok100 && 
                    statusUsina.trackers.ok100 : false;

                  return (
                    <div key={usina.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                      <div className="flex items-center">
                        <div className={`w-6 h-6 rounded-full flex items-center justify-center mr-3 ${
                          hasStatus ? (allOk ? 'bg-green-100' : 'bg-yellow-100') : 'bg-gray-100'
                        }`}>
                          {hasStatus ? (allOk ? '✅' : '⚠️') : '⭕'}
                        </div>
                        <div>
                          <p className="font-medium text-gray-900">{usina.nome}</p>
                          {hasStatus && (
                            <p className="text-xs text-gray-500">
                              {allOk ? 'Todos os sistemas OK' : 'Alguns sistemas com problemas'}
                            </p>
                          )}
                        </div>
                      </div>
                      <div className="text-sm text-gray-500">
                        {hasStatus ? 'Preenchido' : 'Pendente'}
                      </div>
                    </div>
                  );
                })}
            </div>
          ) : (
            <p className="text-sm text-gray-500">
              Selecione um cluster e data para visualizar o status consolidado
            </p>
          )}
        </div>

        {/* Ações */}
        <div className="space-y-3">
          <button
            onClick={generateIndividualWhatsApp}
            disabled={!filtersComplete}
            className="btn-primary w-full flex items-center justify-center"
          >
            <Copy className="w-5 h-5 mr-2" />
            Gerar WhatsApp (Usina Atual)
          </button>

          <button
            onClick={generateConsolidatedWhatsApp}
            disabled={!filters.clusterId || !filters.data}
            className="btn-secondary w-full flex items-center justify-center"
          >
            <Printer className="w-5 h-5 mr-2" />
            Gerar WhatsApp (Cluster Completo)
          </button>

          <button
            onClick={saveStatus}
            disabled={!filtersComplete}
            className="btn-primary w-full"
          >
            Salvar Status Técnico
          </button>
        </div>
      </div>
    </div>
  );
};

export default TechnicalStatusScreen;
