import React, { useState } from 'react';
import { ArrowLeft, Clock, Zap } from 'lucide-react';
import { useData } from '../context/DataContext';
import SharedFilters from './SharedFilters';

const QuickModeScreen = ({ onBack }) => {
  const { data } = useData();

  const [activeTab, setActiveTab] = useState('atividades');

  // Estado compartilhado entre as abas
  const [sharedFilters, setSharedFilters] = useState({
    data: new Date().toISOString().split('T')[0],
    clusterId: '',
    tecnicoIds: []
  });

  return (
    <div className="mobile-container">
      <div className="p-6">
        {/* Header */}
        <div className="flex items-center mb-6">
          <button
            onClick={onBack}
            className="mr-4 p-2 hover:bg-gray-100 rounded-lg transition-colors"
          >
            <ArrowLeft className="w-6 h-6 text-gray-600" />
          </button>
          <div>
            <h1 className="text-xl font-bold text-gray-900">
              Modo Rápido - Relatórios Combinados
            </h1>
            <p className="text-sm text-blue-600">Preencha ambos os relatórios com filtros compartilhados</p>
          </div>
        </div>

        {/* Tabs */}
        <div className="flex bg-gray-100 rounded-lg p-1 mb-6">
          <button
            onClick={() => setActiveTab('atividades')}
            className={`flex-1 py-3 px-4 rounded-md text-sm font-medium transition-colors ${
              activeTab === 'atividades'
                ? 'bg-white text-primary-600 shadow-sm'
                : 'text-gray-500 hover:text-gray-700'
            }`}
          >
            <Clock className="w-4 h-4 inline mr-2" />
            Atividades
          </button>
          <button
            onClick={() => setActiveTab('status')}
            className={`flex-1 py-3 px-4 rounded-md text-sm font-medium transition-colors ${
              activeTab === 'status'
                ? 'bg-white text-primary-600 shadow-sm'
                : 'text-gray-500 hover:text-gray-700'
            }`}
          >
            <Zap className="w-4 h-4 inline mr-2" />
            Status de Equipamentos
          </button>
        </div>

        {/* Conteúdo das Abas */}
        {activeTab === 'atividades' ? (
          <DailyReportScreen onBack={() => {}} quickMode={true} />
        ) : (
          <TechnicalStatusScreen onBack={() => {}} quickMode={true} />
        )}

        {/* Ações Finais do Modo Rápido */}
        <div className="space-y-3 pt-4 border-t border-gray-200 mt-6">
          <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
            <div className="flex items-center">
              <Zap className="w-5 h-5 text-blue-600 mr-2" />
              <div>
                <h3 className="text-sm font-medium text-blue-900 mb-1">
                  Modo Rápido Ativo
                </h3>
                <p className="text-xs text-blue-700">
                  Use as abas acima para alternar entre os relatórios. Cada um mantém seus próprios dados e pode ser salvo independentemente.
                </p>
              </div>
            </div>
          </div>

          <button
            onClick={onBack}
            className="btn-secondary w-full"
          >
            Voltar à Tela Inicial
          </button>
        </div>
      </div>
    </div>
  );
};

export default QuickModeScreen;
